import React, { useState } from "react";
import FormErrors from "../../../Components/FormErrors/FormErrors";
import Button from "../../../Components/Button/Button";
import HttpClient from "../../../Services/HttpClient";

export default function () {
    const [errors, setErrors] = useState([]);
    const [name, setName] = useState("");
    const [description, setDesciption] = useState("");

    const onSubmit = async event => {
        event.preventDefault();
        setErrors([]);
        
        if(!name){
            return setErrors(['Name is required']);
        }
        if (!description){
            return setErrors(['Description is required']);
        }

        const data = {
            name,
            description,
        };

        const response = await HttpClient().post('/api/thread/create', data);
        history.pushState(`/thread/${response.data._id}`);
    }

    return (
        <div className="page">
            <h1 className="page__title">Create Thread</h1>
            <form className="form" onSubmit={onSubmit}>
                {!!errors.length && <FormErrors errors={errors}/>}
                <div className="form__group mb-1">
                    <label className="form__label">Name</label>
                    <input className="form__input" value={name} onChange={e => setName(e.target.value)}/>
                </div>
                <div className="form__group mb-1">
                    <label className="form__label">Description</label>
                    <input className="form__input" value={description} onChange={e => setDesciption(e.target.value)}/>
                </div>
                <Button type="submit">Create</Button>
            </form>
        </div>
    )
    
}
