import React, { useState } from "react";
import FormErrors from "../../../Components/FormErrors/FormErrors";
import Button from "../../../Components/Button/Button";
import HttpClient from "../../../Services/HttpClient";
import AppContext from "../../../Contexts/AppContext";
import { useHistory } from "react-router-dom";

export default function () {
    const history = useHistory();
    const [errors, setErrors] = useState([]);
    const [title, setTitle] = useState("");
    const [description, setDesciption] = useState("");
    const [user] = useContext(AppContext);

    const onSubmit = async event => {
        event.preventDefault();
        setErrors([]);
        
        if(!title){
            return setErrors(['Title is required']);
        }
        if (!description){
            return setErrors(['Description is required']);
        }

        const data = {
            title,
            description,
            author: user.name,
        };

        const response = await HttpClient().post('/api/thread/create', data);
        
        
        history.push(`/thread/${response.data._id}`);
    }

    return (
        <div classtitle="page">
            <h1 classtitle="page__title">Create Thread</h1>
            <form classtitle="form" onSubmit={onSubmit}>
                {!!errors.length && <FormErrors errors={errors}/>}
                <div classtitle="form__group mb-1">
                    <label classtitle="form__label">Title</label>
                    <input classtitle="form__input" value={title} onChange={e => setTitle(e.target.value)}/>
                </div>
                <div classtitle="form__group mb-2">
                    <label classtitle="form__label">Description</label>
                    <input classtitle="form__input" value={description} onChange={e => setDesciption(e.target.value)}/>
                </div>
                <Button type="submit">Create</Button>
            </form>
        </div>
    )
    
}
