import React, { useContext, useEffect, useState } from "react";
import {useParams, useHistory, Link} from 'react-router-dom';
import HttpClient from "../../../Services/HttpClient";
import AppContext from "../../../Contexts/AppContext";
import Button from "../../../Components/Button/Button";
 

export default function(){
    const history = useHistory();
    const {user} = useContext(AppContext);
    const {id} = useParams();
    const [thread, setThread] = useState(null);
    const [comments, setComments] = useState([]);

    useEffect(() => {
        setThread(null);
        setComments([]);
        getThread();
    }, [id]);

    const getThread = async () => {
        const {data} = await HttpClient().get('/api/thread/'+id);
        setThread(data);
        getComments();
    }

    const getComments = async () => {
        const {data} = await HttpClient().get('/api/comment?threadId='+id);
        setComments(data);
    }



    return(
        <div className="page">
            {thread && <h1 className="page__title">{thread.title}</h1>}
            {thread && <p className="page__author">Thread by: {(thread.author && thread.author.name) || "No name"}</p>}
            {thread && <p className="page__description">{thread.description}</p>}

            {user && <Button className="mb-1" onClick={() => history.push(`/comment/create/${id}`)}>Post Comment</Button>}

            
        </div>
    )
}
