import React from "react";
import "./Modal.css";

export default function({children, isOpen}) {
    return(
        <div className={`modal ${!!isOpen ? 'modal--active' : ''}`}>
            <div className="modal__content">
                <span className="modal__close">&times;</span>
                {children}
            </div>
        </div>
    )
}
